// components/Hero.js
import React, { useState, useEffect } from 'react';
import slide1 from '../components/';
import slide2 from '../images/slide2.jpg';
import slide3 from '../images/slide3.jpg';

function Hero() {
    const images = [slide1, slide2, slide3];
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
        }, 3000); // Change slide every 3 seconds

        return () => clearInterval(slideInterval);
    }, [images.length]);

    return (
        <section id="home" className="relative bg-blue-100 min-h-screen flex items-center justify-center text-center px-6 overflow-hidden">
            <div className="absolute inset-0">
                <img 
                    src={images[currentSlide]} 
                    alt="Chemical Slide" 
                    className="w-full h-full object-cover opacity-70 transition-opacity duration-1000 ease-in-out" 
                />
            </div>
            <div className="relative z-10">
                <h1 className="text-3xl md:text-4xl font-bold text-white">Welcome to Kamsy Chemicals</h1>
                <p className="mt-4 text-base md:text-lg text-white">Your trusted source for quality chemicals</p>
                <button className="mt-6 px-6 md:px-8 py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600">
                    Explore Our Products
                </button>
            </div>
        </section>
    );
}

export default Hero;
