// components/Hero.js
import React from 'react';

function Hero() {
    return (
        <section id="home" className="bg-blue-100 py-16 px-6 text-center">
            <h1 className="text-3xl md:text-4xl font-bold text-blue-700">Welcome to Kamsy Chemicals</h1>
            <p className="mt-4 text-base md:text-lg text-gray-700">Your trusted source for quality chemicals</p>
            <button className="mt-6 px-6 md:px-8 py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600">
                Explore Our Products
            </button>
        </section>
    );
}

export default Hero;
