// components/ProductDetail.js
import React from 'react';
import { useParams } from 'react-router-dom';

const productData = Array.from({ length: 20 }, (_, i) => ({
    id: i + 1,
    title: `Product ${i + 1}`,
    description: `This is the description for Product ${i + 1}. It provides details about the features and benefits of the product.`,
    imageUrl: `https://via.placeholder.com/300?text=Product+${i + 1}`
}));

function ProductDetail() {
    const { id } = useParams();
    const product = productData.find((item) => item.id === parseInt(id));

    if (!product) {
        return <p className="text-center mt-10">Product not found.</p>;
    }

    return (
        <section className="min-h-screen bg-gray-100 py-10 px-6 flex flex-col items-center">
            <div className="bg-white rounded-lg shadow-lg p-6 max-w-md w-full">
                <img src={product.imageUrl} alt={product.title} className="w-full h-64 object-cover rounded-lg" />
                <h1 className="text-2xl font-bold text-gray-800 mt-4">{product.title}</h1>
                <p className="text-gray-700 mt-2">{product.description}</p>
                <button className="mt-6 px-4 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600">
                    Buy Now
                </button>
            </div>
        </section>
    );
}

export default ProductDetail;
