// components/Hero.js
import React, { useState, useEffect } from 'react';
import slide1 from '../components/images/acetic-acid.jpeg';
import slide2 from '../components/images/sodium hydroxide.webp';
import slide3 from '../components/images/hydrochloric-acid.jpeg';

function Hero() {
    const images = [slide1, slide2, slide3];
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const slideInterval = setInterval(() => {
            setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
        }, 3000); // Slide changes every 3 seconds

        return () => clearInterval(slideInterval);
    }, [images.length]);

    return (
        <section id="home" className="relative bg-blue-100 min-h-screen flex items-center justify-center text-center overflow-hidden">
            <div className="absolute inset-0 flex transition-transform duration-1000 ease-in-out" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                {images.map((image, index) => (
                    <img
                        key={index}
                        src={image}
                        alt={`Slide ${index + 1}`}
                        className="w-full h-full object-cover"
                    />
                ))}
            </div>
            <div className="relative z-10 bg-black bg-opacity-50 p-6 rounded-lg">
                <h1 className="text-3xl md:text-4xl font-bold text-white">Welcome to Kamsy Chemicals</h1>
                <p className="mt-4 text-base md:text-lg text-white">Your trusted source for quality chemicals</p>
                <button className="mt-6 px-6 md:px-8 py-3 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600">
                    Explore Our Products
                </button>
            </div>
        </section>
    );
}

export default Hero;
