// components/FeaturedProducts.js
import React from 'react';
import ProductCard from './ProductCard';
import sodiumhydroxide from '../components/images/sodium hydroxide.webp'
import aceticacid from '../components'

function FeaturedProducts() {
    const products = [
        { id: 1, name: "Sodium Hydroxide", description: "Used in various industrial applications", image: sodiumhydroxide },
        { id: 2, name: "Acetic Acid", description: "High purity for laboratory use", image: "path-to-image2" },
        { id: 3, name: "Hydrochloric Acid", description: "Widely used chemical in industries", image: "path-to-image3" },
    ];

    return (
        <section id="products" className="py-16 px-6 bg-white text-center">
            <h2 className="text-2xl md:text-3xl font-bold text-blue-700">Our Products</h2>
            <div className="mt-8 grid gap-6 sm:grid-cols-2 md:grid-cols-3">
                {products.map((product) => (
                    <ProductCard key={product.id} product={product} />
                ))}
            </div>
        </section>
    );
}

export default FeaturedProducts;
